document.addEventListener("DOMContentLoaded",(function(){document.querySelectorAll(".project-screenshots").forEach((e=>{const t=e.querySelectorAll(".mySlides");let n=0;function c(){t.forEach((e=>e.style.display="none")),t[n].style.display="block"}c();const o=e.querySelector(".prev"),l=e.querySelector(".next");o.addEventListener("click",(function(){n--,n<0&&(n=t.length-1),c()})),l.addEventListener("click",(function(){n++,n>=t.length&&(n=0),c()}))}))}));